import React, { useState, useEffect } from 'react';
import { Container, Grow, Grid, AppBar, TextField, Button, Paper } from '@material-ui/core';
import { useDispatch } from 'react-redux';
import { useHistory, useLocation } from 'react-router-dom';
import ChipInput from 'material-ui-chip-input';

import { getPostsBySearch, getTopPosts } from '../../actions/posts';
import Posts from '../Posts/Posts';
import Form from '../Form/Form';
import Pagination from '../Pagination';
import useStyles from './styles';

function useQuery() {
  return new URLSearchParams(useLocation().search);
}

const Home = () => {
  const classes = useStyles();
  const query = useQuery();
  const page = query.get('page') || 1;
  const searchQuery = query.get('searchQuery');

  const [currentId, setCurrentId] = useState(0);
  const dispatch = useDispatch();

  const [search, setSearch] = useState('');
  const [tags, setTags] = useState([]);
  const history = useHistory();

  const searchPost = () => {
    if (search.trim() || tags) {
      dispatch(getPostsBySearch({ search, tags: tags.join(',') }));
      history.push(`/posts/search?searchQuery=${search || 'none'}&tags=${tags.join(',')}`);
    } else {
      history.push('/');
    }
  };

  const handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      searchPost();
    }
  };

  const handleAddChip = (tag) => setTags([...tags, tag]);

  const handleDeleteChip = (chipToDelete) => setTags(tags.filter((tag) => tag !== chipToDelete));

  const handleViewTopPosts = () => {
    history.push('/Top5');
  };

  const [topPosts, setTopPosts] = useState([]);

  const fetchTopPosts = async () => {
    try {
      const response = await fetch('http://localhost:5000/posts/top5');
      const data = await response.json();
      setTopPosts(data.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchTopPosts();
  }, []);

  return (
    <Grow in>
      <Container maxWidth="xl">
        <Grid container justify="space-between" alignItems="stretch" spacing={3} className={classes.gridContainer}>
          <Grid item xs={12} sm={6} md={9}>
            {topPosts.length ? (
              <Posts posts={topPosts} setCurrentId={setCurrentId} />
            ) : (
              <p>No top posts to display</p>
            )}
          </Grid>
          <Grid item xs={12} sm={6} md={3}>
            <AppBar className={classes.appBarSearch} position="static" color="inherit">
              <Button onClick={handleViewTopPosts} className={classes.viewTopPostsButton} variant="contained" color="secondary" style={{ margin: '10px 0' }} >View Top Posts</Button>
              <TextField onKeyDown={handleKeyPress} name="search" variant="outlined" label="Search Reviews" fullWidth value={search} onChange={(e) => setSearch(e.target.value)} />
              <ChipInput
                style={{ margin: '10px 0' }}
                value={tags}
                onAdd={(chip) => handleAddChip(chip)}
                onDelete={(chip) => handleDeleteChip(chip)}
                label="Search Reviews by Tags"
                variant="outlined"
              />
              <Button onClick={searchPost} className={classes.searchButton}
              variant="contained" color="primary">Search</Button>
                      </AppBar>
        <Form currentId={currentId} setCurrentId={setCurrentId} />
        {(!searchQuery && !tags.length) && (
          <Paper className={classes.pagination} elevation={6}>
            <Pagination page={page} />
          </Paper>
        )}
      </Grid>
    </Grid>
  </Container>
</Grow>
);
};
