import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { CircularProgress, Grid, Card, CardContent, Typography } from '@material-ui/core';
import { getTopPosts } from '../../actions/posts';

const Top5 = () => {
  const dispatch = useDispatch();
  const { posts, isLoading } = useSelector((state) => state.posts);
  const sortedPosts = [...posts].sort((a, b) => b.likeCount - a.likeCount);

  useEffect(() => {
    dispatch(getTopPosts());
  }, [dispatch]);

  if (isLoading) {
    return (
      <Grid container justify="center" alignItems="center">
        <CircularProgress />
      </Grid>
    );
  }

  return (
    <Grid container spacing={3}>
      {sortedPosts.map((post) => (
        <Grid key={post._id} item xs={12} sm={6} md={4} lg={3}>
          <Card>
            <CardContent>
              <Typography variant="h5" component="h2">
               Review Title :- {post.title}
              </Typography>
              <Typography variant="subtitle1" color="textSecondary">
               Review Creater :- {post.creater}
              </Typography>
              <Typography variant="body2" component="p">
               Review Message :- {post.message}
              </Typography>
              <Typography variant="likes" component="p">
               Total Likes F {post.likeCount}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default Top5;
