import { GET_TOP_POSTS, GET_TOP_POSTS_SUCCESS, GET_TOP_POSTS_FAILURE } from '../constants/actionTypes';
import * as api from '../api/index';

export const getTopPosts = () => async (dispatch) => {
  try {
    dispatch({ type: GET_TOP_POSTS });
    const { data } = await api.fetchTopPosts('top5');

    dispatch({ type: GET_TOP_POSTS_SUCCESS, payload: data }); // Dispatching the success action with the fetched top posts data
  } catch (error) {
    dispatch({ type: GET_TOP_POSTS_FAILURE, payload: error.message }); // Dispatching the failure action with the error message
    console.log(error);
  }
};
